<!--头部组件--><style>.preview-header {    padding-right: 11%;    height: 60px;    background: #4a5161;    text-align: right;}ul {    display: inline-block !important;}</style><template>    <div class="preview-header">        <Menu mode="horizontal" theme="dark" active-name="1" >            <div class="preview-header-nav">                <MenuItem name="1">                    <Icon type="ios-home-outline"></Icon>                    首页                </MenuItem>                <MenuItem name="2">                    <Icon type="ios-keypad"></Icon>                    A类文章                </MenuItem>                <MenuItem name="3">                    <Icon type="ios-analytics"></Icon>                    B类文章                </MenuItem>                <MenuItem name="4">                    <Icon type="ios-paper"></Icon>                    测试                </MenuItem>                <MenuItem name="5">                    <div class="user-dropdown-menu-con">                        <Row type="flex" justify="end" align="middle" class="user-dropdown-innercon">                            <Dropdown transfer trigger="click" @on-click="handleClickUserDropdown">                                <a href="javascript:void(0)">                                    <span class="main-user-name">刘文龙</span>                                    <Icon type="arrow-down-b"></Icon>                                </a>                                <DropdownMenu slot="list">                                    <DropdownItem name="userHome" divided>个人中心</DropdownItem>                                    <DropdownItem name="loginout" divided>退出登录</DropdownItem>                                    <DropdownItem name="loginout" divided>设置</DropdownItem>                                </DropdownMenu>                            </Dropdown>                            <Avatar icon="person" style="background: #619fe7;margin-left:10px;" src="https://ss1.bdstatic.com/70cFvXSh_Q1YnxGkpoWK1HF6hhy/it/u=3448484253,3685836170&amp;fm=27&amp;gp=0.jpg"></Avatar>                        </Row>                    </div>                </MenuItem>            </div>        </Menu>    </div></template><script>    export default {        data () {            return {                navList: []            };        },        methods: {            handleClickUserDropdown: function () {                let a = 'ss';                alert(a);            }        }    };</script>