<style lang="less">    @import '../../styles/common.less';    @import './index.less';</style><template>    <div class="preview-main">        <my-header></my-header>        <Row class="margin-top-20" type="flex" justify="center">            <i-col span="13">                <articleContent></articleContent>            </i-col>            <Col span="5" class="padding-left-10">                <user-info></user-info>                <div class="margin-top-10">                    <Card>                        <p slot="title">                            <Icon type="fireball"></Icon>                            热门文章                        </p>                        <a href="#" slot="extra" @click.prevent="changeLimit">                            <Icon type="more"></Icon>                        </a>                        <div class="preview-placeholderCon">                            <p v-for="item in hotartical"><a>{{item.name}}</a></p>                        </div>                    </Card>                </div>                <div class="margin-top-10">                    <Card>                        <p slot="title">                            <Icon type="ios-lightbulb"></Icon>                            最新文章                        </p>                        <a href="#" slot="extra" @click.prevent="changeLimit" content="更多……">                            <Icon type="more"></Icon>                        </a>                        <ul>                            <li v-for="item in randomMovieList">                                <a :href="item.url" target="_blank">{{ item.name }}</a>                                <span>                                    <Icon type="ios-star" v-for="n in 4" :key="n"></Icon>                                    <Icon type="ios-star" v-if="item.rate >= 9.5"></Icon>                                    <Icon type="ios-star-half" v-else></Icon>                                        {{ item.rate }}                                </span>                            </li>                        </ul>                    </Card>                </div>            </Col>        </Row>        <my-footer></my-footer>    </div></template><script>    import footer from '../commons/footer';    import header from '../commons/header';    import userInfo from '../commons/userInfo';    import util from '../../libs/util';    import articleContent from '../commons/articleContent.vue';    export default {        data () {            return {                hotartical: [                    {'id': '1', 'name': '最新文章……'},                    {'id': '1', 'name': '又一篇新文章！！！'},                    {'id': '1', 'name': '测试'},                    {'id': '1', 'name': '测试'}                ],                movieList: [                    {                        name: 'The Shawshank Redemption',                        url: 'https://movie.douban.com/subject/1292052/',                        rate: 9.6                    },                    {                        name: 'Leon:The Professional',                        url: 'https://movie.douban.com/subject/1295644/',                        rate: 9.4                    },                    {                        name: 'Farewell to My Concubine',                        url: 'https://movie.douban.com/subject/1291546/',                        rate: 9.5                    },                    {                        name: 'Forrest Gump',                        url: 'https://movie.douban.com/subject/1292720/',                        rate: 9.4                    },                    {                        name: 'Life Is Beautiful',                        url: 'https://movie.douban.com/subject/1292063/',                        rate: 9.5                    },                    {                        name: 'Spirited Away',                        url: 'https://movie.douban.com/subject/1291561/',                        rate: 9.2                    },                    {                        name: 'Schindlers List',                        url: 'https://movie.douban.com/subject/1295124/',                        rate: 9.4                    },                    {                        name: 'The Legend of 1900',                        url: 'https://movie.douban.com/subject/1292001/',                        rate: 9.2                    },                    {                        name: 'WALL·E',                        url: 'https://movie.douban.com/subject/2131459/',                        rate: 9.3                    },                    {                        name: 'Inception',                        url: 'https://movie.douban.com/subject/3541415/',                        rate: 9.2                    }                ],                randomMovieList: []            };        },        components: {            'myFooter': footer,            'myHeader': header,            'userInfo': userInfo,            'articleContent': articleContent        },        methods: {            login () {                this.$router.push({                    name: 'login'                });            },            changeLimit () {                function getArrayItems (arr, num) {                    const temp_array = [];                    for (let index in arr) {                        temp_array.push(arr[index]);                    }                    const return_array = [];                    for (let i = 0; i < num; i++) {                        if (temp_array.length > 0) {                            const arrIndex = Math.floor(Math.random() * temp_array.length);                            return_array[i] = temp_array[arrIndex];                            temp_array.splice(arrIndex, 1);                        } else {                            break;                        }                    }                    return return_array;                }                this.randomMovieList = getArrayItems(this.movieList, 5);            }        },        mounted () {            // this.articleTitle = localStorage.articleTitle;            // this.tagsList = localStorage.tagsList ? JSON.parse(localStorage.tagsList) : [];            this.classificationSelected = localStorage.classificationSelected ? JSON.parse(localStorage.classificationSelected) : ['s', 'dsf', 'fsdfs', 'dfsd', 'fsdfsdf'];            // this.content = localStorage.content;            // this.publishTime = localStorage.publishTime;            this.changeLimit();            util.title('你好');        }    };</script>