<style lang="less">    @import '../../styles/common.less';    @import './index.less';</style><template>    <div class="preview-main">        <div class="preview-header">            <Menu mode="horizontal" theme="dark" active-name="1">                <div class="preview-header-nav">                    <MenuItem name="1">                        <Icon type="ios-home-outline"></Icon>                        首页                    </MenuItem>                    <MenuItem name="2">                        <Icon type="ios-keypad"></Icon>                        A类文章                    </MenuItem>                    <MenuItem name="3">                        <Icon type="ios-analytics"></Icon>                        B类文章                    </MenuItem>                    <MenuItem name="4">                        <Icon type="ios-paper"></Icon>                        测试                    </MenuItem>                    <MenuItem name="5">                        <Button @click="login" type="primary" long>管理</Button>                    </MenuItem>                </div>            </Menu>        </div>        <Row class="margin-top-20">            <Col span="17" offset = "1">            <div>                <Card>                    <h1>{{ articleTitle }}</h1>                    <p class="preview-publish-time"><Icon type="android-alarm-clock"></Icon>&nbsp;发布时间：{{ publishTime }}</p>                    <div class="preview-tags-con">                        <b class="preview-tip"><Icon type="ios-pricetags-outline"></Icon>&nbsp;标签：</b><Tag v-for="item in tagsList" type="border" color="blue" :key="item">{{ item }}</Tag>                    </div>                    <div class="preview-content-con" v-html="content"></div>                    <div class="preview-classifition-con">                        <b class="preview-tip"><Icon type="navicon-round"></Icon>&nbsp;目录：</b>                        <a class="preview-classifition-item" v-for="item in classificationSelected" :key="item">                            <Icon type="android-folder-open"></Icon>                            {{ item }}                        </a>                    </div>                </Card>            </div>            </Col>            <Col span="5" class="padding-left-10">            <div>                <Card>                    <p slot="title">                        <Icon type="fireball"></Icon>                        热门文章                    </p>                    <div class="preview-placeholderCon">                        <p v-for="item in hotartical" :key="id"><a>{{item.name}}</a></p>                    </div>                </Card>            </div>            <div class="margin-top-10">                <Card>                    <p slot="title">                        <Icon type="fireball"></Icon>                        热门文章                    </p>                    <div class="preview-placeholderCon">                        <p v-for="item in hotartical" :key="id"><a>{{item.name}}</a></p>                    </div>                </Card>            </div>            <div class="margin-top-10">                <Card>                    <p slot="title">                        <Icon type="ios-lightbulb"></Icon>                        最新文章                    </p>                    <a href="#" slot="extra" @click.prevent="changeLimit">                        <Icon type="ios-loop-strong"></Icon>                        ...                    </a>                    <ul>                        <li v-for="item in randomMovieList">                            <a :href="item.url" target="_blank">{{ item.name }}</a>                            <span>                                <Icon type="ios-star" v-for="n in 4" :key="n"></Icon><Icon type="ios-star" v-if="item.rate >= 9.5"></Icon><Icon type="ios-star-half" v-else></Icon>                                    {{ item.rate }}                            </span>                        </li>                    </ul>                </Card>            </div>            </Col>        </Row>        <footer class="preview-footor">             <p style="color: #cccccc">Copyright © 2017 gitor.org All Rights Reserved</p>             <p style="color: #cccccc;">这就是一个测试的页脚！</p>        </footer>    </div></template><script>export default {  data: function () {    return {      articleTitle: '这是一篇测试文章',      tagsList: ['测试', 'Java', '蛋疼'],      classificationSelected: [],      content: '2015年中，杜某跟他的同伙在电商平台通过虚假交易的方式为他人套现，以收取一定比例手续费的方式牟利。经公安机关侦查，在2015年11月10日至2015年11月13日期间，杜某等人利用多家网上店铺，在全国范围内串通多名电商用户虚构交易共计2500余笔，从“花呗”套现共计470余万元，从中收取手续费共计40余万元。',      publishTime: '2018年01月01日 23:45:00',      hotartical: [            {'id': '1', 'name': '最新文章……'},            {'id': '1', 'name': '又一篇新文章！！！'},            {'id': '1', 'name': '测试'},            {'id': '1', 'name': '测试'}      ],      movieList: [            {                name: 'The Shawshank Redemption',                url: 'https://movie.douban.com/subject/1292052/',                rate: 9.6            },            {                name: 'Leon:The Professional',                url: 'https://movie.douban.com/subject/1295644/',                rate: 9.4            },            {                name: 'Farewell to My Concubine',                url: 'https://movie.douban.com/subject/1291546/',                rate: 9.5            },            {                name: 'Forrest Gump',                url: 'https://movie.douban.com/subject/1292720/',                rate: 9.4            },            {                name: 'Life Is Beautiful',                url: 'https://movie.douban.com/subject/1292063/',                rate: 9.5            },            {                name: 'Spirited Away',                url: 'https://movie.douban.com/subject/1291561/',                rate: 9.2            },            {                name: 'Schindlers List',                url: 'https://movie.douban.com/subject/1295124/',                rate: 9.4            },            {                name: 'The Legend of 1900',                url: 'https://movie.douban.com/subject/1292001/',                rate: 9.2            },            {                name: 'WALL·E',                url: 'https://movie.douban.com/subject/2131459/',                rate: 9.3            },            {                name: 'Inception',                url: 'https://movie.douban.com/subject/3541415/',                rate: 9.2            }      ],  }  },    methods: {        login () {            this.$router.push({                name: 'login'            });        },      changeLimit () {        function getArrayItems(arr, num) {          const temp_array = [];          for (let index in arr) {            temp_array.push(arr[index]);          }          const return_array = [];          for (let i = 0; i<num; i++) {            if (temp_array.length>0) {              const arrIndex = Math.floor(Math.random()*temp_array.length);              return_array[i] = temp_array[arrIndex];              temp_array.splice(arrIndex, 1);            } else {              break;            }          }          return return_array;        }        this.randomMovieList = getArrayItems(this.movieList, 5);      }    },    mounted () {        // this.articleTitle = localStorage.articleTitle;        // this.tagsList = localStorage.tagsList ? JSON.parse(localStorage.tagsList) : [];        this.classificationSelected = localStorage.classificationSelected ? JSON.parse(localStorage.classificationSelected) : ['s','dsf','fsdfs','dfsd','fsdfsdf'];        // this.content = localStorage.content;        // this.publishTime = localStorage.publishTime;        this.changeLimit();    }};</script>